const items = $input.all();
console.log(`[START] Processing ${items.length} transactions`);

// Helper functions
function detectOutliers(values) {
  const sorted = [...values].filter(v => !isNaN(v)).sort((a, b) => a - b);
  const q1 = sorted[Math.floor(sorted.length * 0.25)];
  const q3 = sorted[Math.floor(sorted.length * 0.75)];
  const iqr = q3 - q1;
  const lowerBound = q1 - (1.5 * iqr);
  const upperBound = q3 + (1.5 * iqr);
  return values.map(v => v < lowerBound || v > upperBound);
}

function getAmountBucket(amount) {
  if (amount < 10) return "0-10";
  if (amount < 50) return "10-50";
  if (amount < 100) return "50-100";
  if (amount < 200) return "100-200";
  if (amount < 500) return "200-500";
  if (amount < 1000) return "500-1000";
  return "1000+";
}

function getTimeBucket(time) {
  const hours = time / 3600;
  if (hours < 6) return "0-6h";
  if (hours < 12) return "6-12h";
  if (hours < 18) return "12-18h";
  if (hours < 24) return "18-24h";
  if (hours < 30) return "24-30h";
  if (hours < 36) return "30-36h";
  if (hours < 42) return "36-42h";
  return "42h+";
}

function simpleClustering(data, k = 5) {
  console.log(`[CLUSTERING] Processing ${data.length} items...`);
  const result = [];
  for (let i = 0; i < data.length; i++) {
    const row = data[i].json || data[i];
    const hash = (parseFloat(row.V1 || 0) + parseFloat(row.V2 || 0) + parseFloat(row.V3 || 0)) * 1000;
    result.push(Math.abs(Math.floor(hash)) % k);
  }
  return result;
}

// Helper to get Time from first column
function getTimeFromRow(row) {
  const firstKey = Object.keys(row)[0];
  return parseFloat(row[firstKey]) || 0;
}

// Compute metadata
console.log("[OUTLIERS] Computing outliers...");
const amounts = [];
const times = [];
for (let i = 0; i < items.length; i++) {
  const row = items[i].json || items[i];
  amounts.push(parseFloat(row.Amount || 0));
  times.push(getTimeFromRow(row));
}

const outlierFlags = detectOutliers(amounts);
const timeOutlierFlags = detectOutliers(times);

console.log("[CLUSTERING] Computing clusters...");
const clusterAssignments = simpleClustering(items, 5);

console.log("[ANOMALY] Computing anomaly scores...");
const anomalyScores = [];
let maxScore = 0;
for (let i = 0; i < items.length; i++) {
  const row = items[i].json || items[i];
  let sum = 0;
  for (let j = 1; j <= 28; j++) {
    sum += Math.abs(parseFloat(row[`V${j}`] || 0));
  }
  anomalyScores.push(sum);
  if (sum > maxScore) maxScore = sum;
}

// Normalize scores
const normalizedScores = anomalyScores.map(s => s / maxScore);

// Build output
const output = [];
for (let idx = 0; idx < items.length; idx++) {
  const row = items[idx].json || items[idx];
  const time = getTimeFromRow(row);
  const amount = parseFloat(row.Amount || 0);
  const fraudClass = parseInt(row.Class || 0);

  output.push({
    json: {
      id: `txn_${String(idx).padStart(8, '0')}`,
      Time: time,
      V1: parseFloat(row.V1 || 0),
      V2: parseFloat(row.V2 || 0),
      V3: parseFloat(row.V3 || 0),
      V4: parseFloat(row.V4 || 0),
      V5: parseFloat(row.V5 || 0),
      V6: parseFloat(row.V6 || 0),
      V7: parseFloat(row.V7 || 0),
      V8: parseFloat(row.V8 || 0),
      V9: parseFloat(row.V9 || 0),
      V10: parseFloat(row.V10 || 0),
      V11: parseFloat(row.V11 || 0),
      V12: parseFloat(row.V12 || 0),
      V13: parseFloat(row.V13 || 0),
      V14: parseFloat(row.V14 || 0),
      V15: parseFloat(row.V15 || 0),
      V16: parseFloat(row.V16 || 0),
      V17: parseFloat(row.V17 || 0),
      V18: parseFloat(row.V18 || 0),
      V19: parseFloat(row.V19 || 0),
      V20: parseFloat(row.V20 || 0),
      V21: parseFloat(row.V21 || 0),
      V22: parseFloat(row.V22 || 0),
      V23: parseFloat(row.V23 || 0),
      V24: parseFloat(row.V24 || 0),
      V25: parseFloat(row.V25 || 0),
      V26: parseFloat(row.V26 || 0),
      V27: parseFloat(row.V27 || 0),
      V28: parseFloat(row.V28 || 0),
      Amount: amount,
      transaction_id: `txn_${String(idx).padStart(8, '0')}`,
      class: fraudClass,
      class_label: fraudClass === 0 ? "legitimate" : "fraud",
      amount_bucket: getAmountBucket(amount),
      time_bucket: getTimeBucket(time),
      is_outlier: outlierFlags[idx] || timeOutlierFlags[idx],
      cluster_id: clusterAssignments[idx],
      anomaly_score: parseFloat(normalizedScores[idx].toFixed(4)),
      anomaly_level:
        normalizedScores[idx] > 0.8
          ? "high"
          : normalizedScores[idx] > 0.5
          ? "medium"
          : "low",
    },
  });
}

return output;

const items = $input.all();
console.log(`Creating embeddings for ${items.length} items`);

const embeddings = [];

for (let i = 0; i < items.length; i++) {
  const data = items[i].json;

  const features = [];

  // 1Ô∏è‚É£ Add normalized Time first
  const time = parseFloat(data.Time || 0);
  features.push(time / 172800); // Normalize by ~2 days

  // 2Ô∏è‚É£ Add V1-V28 PCA features
  for (let j = 1; j <= 28; j++) {
    features.push(parseFloat(data[`V${j}`] || 0));
  }

  // 3Ô∏è‚É£ Add normalized Amount
  const amount = parseFloat(data.Amount || 0);
  features.push(Math.log(amount + 1) / 10);

  // 4Ô∏è‚É£ Add Class (0 or 1)
  features.push(parseFloat(data.class || 0));

  // Now features.length === 31
  embeddings.push({
    json: {
      id: data.id || data.transaction_id || `txn_${String(i).padStart(8, '0')}`,
      values: features,
      metadata: {
        transaction_id: data.transaction_id || data.id,
        class: data.class,
        class_label: data.class_label,
        amount: data.Amount,
        amount_bucket: data.amount_bucket,
        time: data.Time,
        time_bucket: data.time_bucket,
        is_outlier: data.is_outlier,
        cluster_id: data.cluster_id,
        pca_cluster: `cluster_${data.cluster_id}`,
        anomaly_score: data.anomaly_score,
        anomaly_level: data.anomaly_level,
        record_type: "transaction"
      }
    }
  });

  if ((i + 1) % 10000 === 0) {
    console.log(`Created ${i + 1} / ${items.length} embeddings`);
  }
}

console.log(`Created ${embeddings.length} embedding vectors`);
console.log(`Vector dimension: ${embeddings[0].json.values.length}`); // Should print 31
console.log(`Sample vector (first 5 dims): ${embeddings[0].json.values.slice(0, 5).map(v => v.toFixed(3)).join(', ')}`);

return embeddings;

const item = $input.first().json;
const output = item.output;
const jsonMatch = output.match(/```json\s*([\s\S]*?)```/);
let structuredData = null;
let textResponse = output;
if (jsonMatch && jsonMatch[1]) {
  try {
    structuredData = JSON.parse(jsonMatch[1].trim());
    textResponse = output.replace(/```json[\s\S]*?```/g, '').trim();
  } catch (e) {
    console.log('Failed to parse JSON:', e.message);
  }
}
return {
  textResponse: textResponse,
  hasStructuredData: !!structuredData,
  structuredData: structuredData,
  rawOutput: output
};

const item = $input.first().json;
const data = item.structuredData;
if (!data || !data.data) {
  return item;
}
if (data.data.labels && data.data.values) {
  if (data.data.labels.length !== data.data.values.length) {
    throw new Error('Labels and values arrays must have the same length');
  }
}
const chartConfig = {
  type: data.recommendedVisualization || 'bar',
  title: data.title,
  labels: data.data.labels,
  values: data.data.values,
  metadata: data.data.metadata || {},
  insights: data.insights || []
};
if (chartConfig.type === 'pie' || chartConfig.type === 'doughnut') {
  chartConfig.colors = ['#4ECDC4', '#FF6B6B', '#45B7D1', '#FFA07A', '#98D8C8'];
} else if (chartConfig.type === 'bar') {
  chartConfig.colors = ['#667eea'];
} else if (chartConfig.type === 'line') {
  chartConfig.colors = ['#667eea'];
}
return {
  textResponse: item.textResponse,
  structuredData: data,
  chartConfig: chartConfig,
  readyForVisualization: true
};

const item = $input.first().json;
const config = item.chartConfig;
if (!config) {
  return item;
}
const quickChartConfig = {
  type: config.type,
  data: {
    labels: config.labels,
    datasets: [{
      label: config.title,
      data: config.values,
      backgroundColor: config.colors[0],
      borderColor: config.colors[0],
      borderWidth: 2,
      fill: config.type === 'line' ? false : true
    }]
  },
  options: {
    title: {
      display: true,
      text: config.title,
      fontSize: 16
    },
    legend: {
      display: config.type === 'pie' || config.type === 'doughnut'
    },
    responsive: true,
    maintainAspectRatio: true
  }
};
if (config.type === 'pie' || config.type === 'doughnut') {
  quickChartConfig.data.datasets[0].backgroundColor = config.colors;
}
const encodedConfig = encodeURIComponent(JSON.stringify(quickChartConfig));
const chartUrl = `https://quickchart.io/chart?c=${encodedConfig}&width=800&height=500&backgroundColor=white&format=png`;
return {
  ...item,
  chartUrl: chartUrl
};

const item = $input.first().json;
const hasChart = item.chartUrl && item.chartUrl.length > 0;
const response = {
  text: item.textResponse,
  hasVisualization: hasChart,
  data: item.structuredData || null,
  chartUrl: hasChart ? item.chartUrl : null,
  insights: item.structuredData?.insights || [],
  metadata: item.structuredData?.data?.metadata || {}
};
return response;

const item = $input.first().json;
return {
  text: item.textResponse,
  hasVisualization: false,
  data: null,
  chartUrl: null,
  insights: [],
  metadata: {}
};

