=<!DOCTYPE html>
<html lang="pt-BR">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Upload ZIP para Google Drive</title>
    <style>
        * { margin: 0; padding: 0; box-sizing: border-box; }
        body {
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            min-height: 100vh;
            display: flex;
            justify-content: center;
            align-items: center;
            padding: 20px;
        }
        .container {
            background: white;
            padding: 40px;
            border-radius: 20px;
            box-shadow: 0 20px 60px rgba(0,0,0,0.3);
            max-width: 500px;
            width: 100%;
        }
        h1 {
            color: #333;
            margin-bottom: 10px;
            font-size: 28px;
        }
        .subtitle {
            color: #666;
            margin-bottom: 30px;
            font-size: 14px;
        }
        .form-group {
            margin-bottom: 25px;
        }
        label {
            display: block;
            margin-bottom: 8px;
            color: #555;
            font-weight: 600;
            font-size: 14px;
        }
        input[type="text"] {
            width: 100%;
            padding: 12px 15px;
            border: 2px solid #e0e0e0;
            border-radius: 8px;
            font-size: 14px;
            transition: all 0.3s;
        }
        input[type="text"]:focus {
            outline: none;
            border-color: #667eea;
            box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
        }
        .hint {
            font-size: 12px;
            color: #888;
            margin-top: 5px;
        }
        button {
            width: 100%;
            padding: 14px;
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            color: white;
            border: none;
            border-radius: 8px;
            font-size: 16px;
            font-weight: 600;
            cursor: pointer;
            transition: transform 0.2s, box-shadow 0.2s;
        }
        button:hover {
            transform: translateY(-2px);
            box-shadow: 0 10px 25px rgba(102, 126, 234, 0.4);
        }
        button:active {
            transform: translateY(0);
        }
        .result {
            margin-top: 20px;
            padding: 15px;
            border-radius: 8px;
            display: none;
        }
        .result.success {
            background: #d4edda;
            color: #155724;
            border: 1px solid #c3e6cb;
        }
        .result.error {
            background: #f8d7da;
            color: #721c24;
            border: 1px solid #f5c6cb;
        }
        .loading {
            display: none;
            text-align: center;
            margin-top: 20px;
        }
        .spinner {
            border: 3px solid #f3f3f3;
            border-top: 3px solid #667eea;
            border-radius: 50%;
            width: 40px;
            height: 40px;
            animation: spin 1s linear infinite;
            margin: 0 auto;
        }
        @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
        }
    </style>
</head>
<body>
    <div class="container">
        <h1>üì§ Upload ZIP para Drive</h1>
        <p class="subtitle">Envie seu arquivo ZIP para o Google Drive</p>

        <form id="uploadForm">
            <div class="form-group">
                <label for="filePath">Caminho do Arquivo ZIP</label>
                <input 
                    type="text" 
                    id="filePath" 
                    name="filePath" 
                    placeholder="C:\Users\SeuNome\arquivo.zip"
                    required
                >
                <div class="hint">Windows: C:\pasta\arquivo.zip | Linux/Mac: /home/user/arquivo.zip</div>
            </div>

            <div class="form-group">
                <label for="fileName">Nome no Google Drive (opcional)</label>
                <input 
                    type="text" 
                    id="fileName" 
                    name="fileName" 
                    placeholder="meu-arquivo.zip"
                >
                <div class="hint">Deixe em branco para usar o nome original</div>
            </div>

            <button type="submit">üöÄ Fazer Upload</button>
        </form>

        <div class="loading">
            <div class="spinner"></div>
            <p style="margin-top: 10px; color: #666;">Enviando arquivo...</p>
        </div>

        <div class="result" id="result"></div>
    </div>

    <script>
        document.getElementById('uploadForm').addEventListener('submit', async (e) => {
            e.preventDefault();

            const filePath = document.getElementById('filePath').value;
            const fileName = document.getElementById('fileName').value;
            const resultDiv = document.getElementById('result');
            const loadingDiv = document.querySelector('.loading');
            const form = document.getElementById('uploadForm');

            // Mostrar loading
            form.style.display = 'none';
            loadingDiv.style.display = 'block';
            resultDiv.style.display = 'none';

            try {
                const response = await fetch('/webhook/upload-zip-api', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ filePath, fileName })
                });

                const data = await response.json();

                loadingDiv.style.display = 'none';
                resultDiv.style.display = 'block';

                if (data.success) {
                    resultDiv.className = 'result success';
                    resultDiv.innerHTML = `
                        <strong>‚úÖ Upload realizado com sucesso!</strong><br><br>
                        <strong>Arquivo:</strong> ${data.fileName}<br>
                        <strong>ID:</strong> ${data.fileId}<br>
                        <a href="${data.webViewLink}" target="_blank" style="color: #155724; font-weight: 600;">üìÇ Ver no Google Drive</a>
                    `;
                } else {
                    throw new Error(data.error || 'Erro desconhecido');
                }

                // Resetar form ap√≥s 3 segundos
                setTimeout(() => {
                    form.style.display = 'block';
                    form.reset();
                }, 3000);

            } catch (error) {
                loadingDiv.style.display = 'none';
                resultDiv.style.display = 'block';
                resultDiv.className = 'result error';
                resultDiv.innerHTML = `<strong>‚ùå Erro:</strong> ${error.message}`;
                form.style.display = 'block';
            }
        });
    </script>
</body>
</html>
